var Slider={slideState:0,run:function(){Slider.naturalStates();Slider.clickNext();Slider.clickPrevious();Slider.clickSquare();},clickNext:function(){$('.nav-right').click(function(){Slider.slideLeftFull();});},clickPrevious:function(){$('.nav-left').click(function(){Slider.slideRightFull();});},clickSquare:function(){$('.slider-block').click(function(){var selected=$(this).index()+1;var current=Slider.slideState+1;var difference;if(selected==current){}else if(selected>current){difference=selected-current;for(var i=0;i<difference;i++){Slider.slideLeftFull();}}else if(selected<current){difference=current-selected;for(var j=0;j<difference;j++){Slider.slideRightFull();}}});},block:$('.slider-block'),naturalStates:function(){$('.nav-left').fadeTo(0,0.33);},sliderState:function(){if(Slider.slideState===0){$('.nav-left').fadeTo(0,1);}else if(Slider.slideState==3){$('.nav-right').fadeTo(0,1);}},slideLeft:function(){$('.slider-full').animate({marginLeft:'-=542px'},200);},slideRight:function(){$('.slider-full').animate({marginLeft:'+=542px'},200);},slideLeftFull:function(){if(Slider.slideState<3){Slider.androidControl('left');Slider.sliderState();Slider.slideLeft();Slider.slideState++;Slider.block.removeClass('active').eq(Slider.slideState).addClass('active');if(Slider.slideState===3){$('.nav-right').fadeTo(0,0.33);}}},slideRightFull:function(){if(Slider.slideState>0){Slider.androidControl('right');Slider.sliderState();Slider.slideRight();Slider.slideState--;Slider.block.removeClass('active').eq(Slider.slideState).addClass('active');if(Slider.slideState===0){$('.nav-left').fadeTo(0,0.33);}}},androidControl:function(direction){var inverted;inverted=3-Slider.slideState;if(direction==='left'){$('.android-img').children().eq(inverted).fadeTo(100,0);}else if(direction==='right'){inverted=inverted+1;$('.android-img').children().eq(inverted).fadeTo(100,1);}}};
